mapper.select:
   select * from member
	  where id = #{findStr}
	  or    phone like '%${findStr}%'
	  or    name  like '%${findStr}%'
	  limit #{startNo}, #{listSize}

select id, name, gender, phone, oriFile, sysFile, 
date_format(mdate, '%Y-%m-%d') mdate 
from member where id=#{id}

mapper.update:
update member set name = #{name}, gender = #{gender}, phone = #{phone}, mdate=#{mdate}

		 , oriFile = #{oriFile}, sysFile = #{sysFile}

where id = #{id}
